describe('<UnableToChargeModal />', () => {
  beforeEach(() => {
    jest.clearAllMocks();
    // Mock window.alert
    window.alert = jest.fn();
  });

  describe('Component Rendering', () => {
    it('should render the modal with correct content when activeRenewalFailureData and paymentMethod are provided', () => {
      render(
        <MockedProvider>
          <UnableToChargeModal
            activeRenewalFailureData={mockActiveRenewalFailureData}
            paymentMethod={PaymentMethod.CreditCard}
          />
        </MockedProvider>
      );

      expect(screen.getByTestId('UnableToChargeModal')).toBeInTheDocument();
      expect(
        screen.getByText('Unable to Charge Credit Card')
      ).toBeInTheDocument();
      expect(
        screen.getByText(
          'Our payment system was not able to successfully process the payment using the credit card provided.'
        )
      ).toBeInTheDocument();
      expect(
        screen.getByText(
          'You may try to proceed by manually entering the credit card info with Stripe or by sending an invoice to the client.'
        )
      ).toBeInTheDocument();
      expect(screen.getByTestId('proceedToStripeButton')).toBeInTheDocument();
      expect(
        screen.getByTestId('sendInvoiceToClientButton')
      ).toBeInTheDocument();
    });

    it('should render Loading component when isLoading is true', async () => {
      render(
        <MockedProvider mocks={[sendInvoiceLinkMockSuccess]}>
          <UnableToChargeModal
            activeRenewalFailureData={mockActiveRenewalFailureData}
            paymentMethod={PaymentMethod.CreditCard}
          />
        </MockedProvider>
      );

      const sendInvoiceButton = screen.getByTestId('sendInvoiceToClientButton');
      await act(async () => {
        userEvent.click(sendInvoiceButton);
      });

      expect(screen.getByTestId('Loading')).toBeInTheDocument();
    });

    it('should render PaymentCompleteMessage when completionData is available', async () => {
      render(
        <MockedProvider mocks={[createPaymentSessionMockCompletion]}>
          <UnableToChargeModal
            activeRenewalFailureData={mockActiveRenewalFailureData}
            paymentMethod={PaymentMethod.CreditCard}
          />
        </MockedProvider>
      );

      const proceedButton = screen.getByTestId('proceedToStripeButton');
