        userEvent.click(proceedButton);
      });

      await waitFor(() => {
        expect(screen.getByTestId('PaymentCompleteMessage')).toBeInTheDocument();
      });
    });

    it('should render SimpleQuoteExpired when isRenewalExpired is true', async () => {
      render(
        <MockedProvider mocks={[createPaymentSessionMockCancelled]}>
          <UnableToChargeModal
            activeRenewalFailureData={mockActiveRenewalFailureData}
            paymentMethod={PaymentMethod.CreditCard}
          />
        </MockedProvider>
      );

      const proceedButton = screen.getByTestId('proceedToStripeButton');
