        userEvent.click(proceedButton);
      });

      await waitFor(() => {
        expect(screen.getByTestId('SimpleQuoteExpired')).toBeInTheDocument();
      });
    });
  });

  describe('Error Handling', () => {
    it('should throw error when activeRenewalFailureData is undefined', () => {
      const consoleSpy = jest.spyOn(console, 'error').mockImplementation();

      expect(() => {
        render(
          <MockedProvider>
            <UnableToChargeModal
              activeRenewalFailureData={undefined}
              paymentMethod={PaymentMethod.CreditCard}
            />
          </MockedProvider>
        );
      }).toThrow(
        'activeRenewalFailureData or paymentMethod is required in UnableToChargeModal'
      );

      consoleSpy.mockRestore();
    });

    it('should throw error when paymentMethod is undefined', () => {
      const consoleSpy = jest.spyOn(console, 'error').mockImplementation();

      expect(() => {
        render(
          <MockedProvider>
            <UnableToChargeModal
              activeRenewalFailureData={mockActiveRenewalFailureData}
              paymentMethod={undefined}
            />
          </MockedProvider>
        );
      }).toThrow(
        'activeRenewalFailureData or paymentMethod is required in UnableToChargeModal'
      );

      consoleSpy.mockRestore();
    });

    it('should throw error when __typename is not ActiveRenewalFailureData', () => {
      const consoleSpy = jest.spyOn(console, 'error').mockImplementation();
      const invalidData = {
        ...mockActiveRenewalFailureData,
        __typename: 'InvalidType' as any
      };

      expect(() => {
        render(
          <MockedProvider>
            <UnableToChargeModal
              activeRenewalFailureData={invalidData}
              paymentMethod={PaymentMethod.CreditCard}
            />
          </MockedProvider>
        );
      }).toThrow('ActiveRenewalFailureData is invalid in UnableToChargeModal');

      consoleSpy.mockRestore();
    });
  });

  describe('Button Interactions', () => {
    it('should call createPaymentSession when Proceed to Stripe button is clicked', async () => {
      const mockStripe = {
        redirectToCheckout: jest.fn().mockResolvedValue({ error: null })
