            activeRenewalFailureData={mockActiveRenewalFailureData}
            paymentMethod={PaymentMethod.CreditCard}
          />
        </MockedProvider>
      );

      const sendInvoiceButton = screen.getByTestId('sendInvoiceToClientButton');
      await act(async () => {
        userEvent.click(sendInvoiceButton);
      });

      await waitFor(() => {
        expect(window.alert).toHaveBeenCalledWith(
          'Invoice link has been sent to the client'
        );
      });
    });
  });

  describe('Payment Session Creation', () => {
    it('should handle StripeData response and redirect to Stripe', async () => {
      const mockStripe = {
        redirectToCheckout: jest.fn().mockResolvedValue({ error: null })
      };
      const { loadStripe } = require('@stripe/stripe-js');
      loadStripe.mockResolvedValue(mockStripe);

      render(
        <MockedProvider mocks={[createPaymentSessionMockSuccess]}>
          <UnableToChargeModal
            activeRenewalFailureData={mockActiveRenewalFailureData}
            paymentMethod={PaymentMethod.CreditCard}
          />
        </MockedProvider>
      );

      const proceedButton = screen.getByTestId('proceedToStripeButton');
      await act(async () => {
        userEvent.click(proceedButton);
      });

      await waitFor(() => {
        expect(loadStripe).toHaveBeenCalledWith('pk_test_us_key');
      });

      await waitFor(() => {
        expect(mockStripe.redirectToCheckout).toHaveBeenCalledWith({
          sessionId: 'cs_test_session_id'
        });
      });
    });

    it('should use CA publishable key for Canadian Stripe platform', async () => {
      const mockStripe = {
        redirectToCheckout: jest.fn().mockResolvedValue({ error: null })
      };
      const { loadStripe } = require('@stripe/stripe-js');
