              __typename: 'StripeData',
              session_id: 'cs_test_session_id',
              stripePlatformAccount: 'StripePlatformAccount-CA'
            }
          }
        }
      };

      render(
        <MockedProvider mocks={[caStripeData]}>
          <UnableToChargeModal
            activeRenewalFailureData={mockActiveRenewalFailureData}
            paymentMethod={PaymentMethod.CreditCard}
          />
        </MockedProvider>
      );

      const proceedButton = screen.getByTestId('proceedToStripeButton');
      await act(async () => {
        userEvent.click(proceedButton);
      });

      await waitFor(() => {
        expect(loadStripe).toHaveBeenCalledWith('pk_test_ca_key');
      });
    });

    it('should handle Stripe loading failure', async () => {
      const { loadStripe } = require('@stripe/stripe-js');
      loadStripe.mockResolvedValue(null);

      const consoleSpy = jest.spyOn(console, 'error').mockImplementation();

      render(
